function loadPosts(){$.getJSON("data/content.json",function(t){$("#lightPagination").pagination({dataSource:t.posts,autoHidePrevious:!0,autoHideNext:!0,pageSize:5,callback:function(t,e){$("#posts").empty();$.each(t,function(t,e){let n=`\n<div class="card news-line-item mb-2">\n  <div class="card-body">\n    <a onclick="${e.link}" href="${e.href}" target="${e.target}"><h6 class="card-title brown-text">${e.title}</h6></a>\n    <p class="text-justify post-description">${e.description}</p>\n    <span class="small float-right">${e.posted}</span>\n  </div>\n</div>\n  `;document.getElementById("posts").innerHTML+=n}),document.querySelector(".paginationjs-pages").addEventListener("click",()=>{goToDiv("posts")})}})})}fetch("data/content.json").then(t=>t.json()).then(t=>{document.getElementById("carousel-view").remove(),t.top.forEach(t=>{let e=`\n<div class="carousel-item ${t.isactive}">\n  <div class="view">\n  <a onclick="${t.link}" href="${t.href}" target="${t.target}">\n    <div class="caption w-100">\n      <h5 class="title-responsive text-justify">${t.title}</h5>\n    </div>\n    <img class="d-block carousel-img lazy img-fluid w-100" data-src="${t.img}" alt="${t.title}" loading="lazy">\n    </a>\n  </div>\n</div>\n  `;document.getElementById("carousel-inner").innerHTML+=e}),lazyLoadInstance.update()}),loadPosts();
