fetch("data/content.json").then(e=>e.json()).then(e=>{document.getElementById("carousel-view").remove(),e.top.forEach(e=>{let n=`\n          <div class="carousel-item ${e.isactive}">\n            <div class="view">\n              <div class="caption w-100">\n                <a onclick="${e.link}" href="${e.href}">\n                  <h2 class="h3-responsive text-justify">${e.title}</h2>\n                </a>\n              </div>\n              <img class="d-block carousel-img lazy img-fluid w-100" data-src="${e.img}" alt="${e.title}" loading="lazy">\n            </div>\n          </div>\n          `;document.getElementById("carousel-inner").innerHTML+=n}),lazyLoadInstance.update()}),$.getJSON("data/content.json",function(e){$("#lightPagination").pagination({dataSource:e.posts,autoHidePrevious:!0,autoHideNext:!0,pageSize:5,callback:function(e,n){$("#posts").empty();$.each(e,function(e,n){let t=`\n              <div class="card news-line-item mb-2">\n                  <div class="card-body">\n                    <a onclick="${n.link}" href="${n.href}">\n                      <h4 class="card-title brown-text">${n.title}</h4>\n                    </a>\n                    <p class="text-justify post-description">${n.description}</p>\n                    <span class="small float-right">${n.posted}</span>\n                  </div>\n                </div>\n              `;document.getElementById("posts").innerHTML+=t}),document.querySelector(".paginationjs-pages").addEventListener("click",()=>{document.getElementById("news-line").scrollIntoView()})}})});
