fetch("data/content.json").then(n=>n.json()).then(n=>{document.getElementById("carousel-inner").innerHTML="",n.top.forEach(n=>{let e=`\n          <div class="carousel-item ${n.isactive}">\n            <div class="view">\n              <div class="caption w-100">\n                <a onclick="${n.link}" href="${n.href}">\n                  <h3 class="h3-responsive text-justify">${n.title}</h3>\n                </a>\n              </div>\n              <img class="d-block carousel-img lazy img-fluid w-100" data-src="${n.img}" loading="lazy">\n            </div>\n          </div>\n          `;document.getElementById("carousel-inner").innerHTML+=e}),lazyLoadInstance.update()}),$.getJSON("data/content.json",function(n){$("#lightPagination").pagination({dataSource:n.posts,autoHidePrevious:!0,autoHideNext:!0,pageSize:7,callback:function(n,e){$("#posts").empty();$.each(n,function(n,e){let t=`\n              <div class="card news-line-item mb-2">\n                  <div class="card-body">\n                    <a onclick="${e.link}" href="${e.href}">\n                      <h5 class="card-title brown-text">${e.title}</h5>\n                    </a>\n                    <p class="text-justify post-description">${e.description}</p>\n                    <span class="small float-right">${e.posted}</span>\n                  </div>\n                </div>\n              `;document.getElementById("posts").innerHTML+=t}),document.querySelector(".paginationjs-pages").addEventListener("click",()=>{document.getElementById("news-line").scrollIntoView()})}})});
